// liftA3 :: (Applicative f) => (a1 -> a2 -> a3 -> b) -> f a1 -> f a2 -> f a3 -> f b
export const liftA3 = curry((fn, a1, a2, a3) => a1.map(fn).ap(a2).ap(a3));

