  constructor(x) {
    this.$value = x;
  }

  get isLeft() {
    return false;
  }

  get isRight() {
    return true;
  }

  static of(x) {
    throw new Error('`of` called on class Right (value) instead of Either (type)');
  }

  // [util.inspect.custom]() {
